import React, { useState, useEffect } from "react";

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [filteredReviews, setFilteredReviews] = useState([]);
  const [topics, setTopics] = useState([]);
  const [search, setSearch] = useState("");
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [visibleCount, setVisibleCount] = useState(10);

  useEffect(() => {
    fetch("/cochrane_reviews.json")
      .then((res) => res.json())
      .then((data) => {
        setReviews(data);
        setFilteredReviews(data);
        setTopics([...new Set(data.flat().map((review) => review.topic))]);
      });
  }, []);
  console.log(filteredReviews)
  const handleSearch = (e) => {
    const query = e.target.value;
    setSearch(query);
    setShowSuggestions(query.length > 0);
  };

  const selectTopic = (topic) => {
    setSearch(topic);
    setFilteredReviews(reviews.filter((review) => review.Topic === topic));
    setShowSuggestions(false);
    setVisibleCount(10);
  };

  const loadMore = () => {
    setVisibleCount((prev) => prev + 10);
  };

  return (
    <div className="container">
      <h1>Cochrane Library Reviews</h1>
      <div className="search-box">
        <input
          type="text"
          value={search}
          onChange={handleSearch}
          placeholder="Search by Topic..."
          style={{ borderColor: "#962d91" }}
        />
        {showSuggestions && (
          <ul className="suggestions">
            {topics
              .filter((topic) => topic.toLowerCase().includes(search.toLowerCase()))
              .map((topic) => (
                <li key={topic} onClick={() => selectTopic(topic)}>
                  {topic}
                </li>
              ))}
          </ul>
        )}
      </div>
      <ul className="review-list">
        {reviews.slice(0, visibleCount).map((review, index) => (
          <li key={index}>
            <a href={review.URL} target="_blank" rel="noopener noreferrer">
              {review.Title}
            </a>
            <p>{review.Author} - <span style={{ color: "#962d91" }}>{review.Date}</span></p>
          </li>
        ))}
      </ul>
      {visibleCount < filteredReviews.length && (
        <button className="load-more" onClick={loadMore}>Load More</button>
      )}
    </div>
  );
};

export default App;
